import React, {useEffect, useState} from 'react'
import axios from 'axios'

function App() {

  const [todo , setTodo] = useState<any>([])
  const [todoId , setTodoId] = useState<any>("")

  const [task , setTask] = useState('')
  const [description , setDescription] = useState('')
  const [status , setStatus] = useState('')

  const [archived , setArchived] = useState(false)

  useEffect(() => {
    getData()
    console.log()
  },[])

  const getData = () => {
    axios.get("http://localhost:8000/todo")
    .then(res => {
      console.log(res)
      setTodo(res.data.todo)
      console.log(todo)
    })
    .catch(err => {
      console.log(err)
    })
  }

  const postData = () => {
    axios.post('http://localhost:8000/todo' , {
      task,
      description,
      status,
      priority,
      archived
    })
  }

  const archive = () => {
    axios.put(`http://localhost:8000/todo?_id=${todoId}`, {

    })
    .then((res:any) => {
      res.json()
    })
    .catch(err => {
      console.log(err)
    })
  }

  const finishedTask = () => {
    axios.put(`http://localhost:8000/todo?_id=`,{
    status: true
    })
    .then((res) => {
      console.log(res)
    })
    .catch(err => {
      console.log(err)
    })
  }

  return (
    <div>
      <h1>Todo App</h1>

      <div>
        <h2>Tasks</h2>
        {todo.map((data:any) => (
          <>


          {data.status === false ? (

          <form>
          <div className='todo'>
          <div>


            <h3>Task:</h3>
            <p>{data.task}</p>
          </div>

          <div>
              <h3>Description:</h3>
              <p>{data.description}</p>
          </div>

          <div>
              <h3>Status:</h3>
              <p>{data.status.toString()}</p>
          </div>

          <div>
              <h3>Priority:</h3>
              <p>{data.priority}</p>
          </div> 

          <input type="text" name="_id" id="" value={data._id} onChange={(e) => setTodoId(data._id)}/>
          <button onClick={archive}>Archive</button>
          <button>Done</button>

          </div>
          </form>
            )

          : (
            <>
            <h2>Done Tasks</h2>
            <div className='done'>

            <div>
            <h3>Task:</h3>
            <p>{data.task}</p>
          </div>

          <div>
              <h3>Description:</h3>
              <p>{data.description}</p>
          </div>

          <div>
              <h3>Status:</h3>
              <p>{data.status.toString()}</p>
          </div>

          <div>
              <h3>Priority:</h3>
              <p>{data.priority}</p>
          </div> 

          <input type="hidden" name="_id" id="" value={data._id}/>

          <button onClick={archive}>Archive</button>
          <button>Done</button>
            </div>


            </>
          )
          }
            </>

        ))

        }
      </div>


    </div>
  )
}

export default App
